package mypackage;

public class NestedIf {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int age=18;
		double salary=20000;
		
		
		if(age<=18)
		{
			System.out.println("1");
		if(salary>10000)
		{
			System.out.println("2");
		}
		else
		{
			System.out.println("4");
		}
		}
		
		else
			
		{
			System.out.println("my bad");
		}
	
	}
}

